import React, { Component } from 'react';

class BlogsWrite extends Component {
    state = {
        title: '',
        text: '',
    };

    onChangeTitle = (ev) => {
        this.setState({
            [ev.target.title]: ev.target.value
        });
    }

    onChangeText = (ev) => {
        this.setState({
            [ev.target.text]: ev.target.value
        });
    }

    // onChangeTitle = (ev) => {
    //     this.setState({
    //         title: ev.target.value,
    //     });
    // }
    // onChangeText = (ev) => {
    //     this.setState({
    //         text: ev.target.value,
    //     });
    // }

    submit = () => {
        if (this.state.text !== ''){
            const formData = {
                title: this.setState.title,
                text: this.setState.text,
            };

            fetch('http://localhost:8000/api/bloglist', {
                method: 'POST',
                // body: JSON.stringify(formData),
            })
            .then(response => response.json())
            .then(data => {
                console.log("did data come back?", data);
                this.props.history.push('/');
            });
        }
    }

    // submit = () => {
    //     const formData = {
    //         title: this.state.title,
    //         text: this.state.text,
    //     };

    //     fetch('http://localhost:8000/api/bloglist', {
    //         method: 'POST',
    //     }).then(response => response.json())
    //     .then(data => {});
    // }

    render() {
        return (
            <form>
                <h2>Title</h2>
                <input 
                    placeholder="Title goes here."
                    onChangeContent={this.onChangeTitle} />
                <br />
                <h2>Blog</h2>
                <textarea 
                    placeholder="Write blog here."
                    onChangeContent={this.onChangeText} />
                <br />
                <p>This where blogs are written</p>
                <button onClick={this.submit}>
                    Post
                </button>
            </form>
        );
    }
}

export default BlogsWrite;
