    getBlogs = () => {
        console.log('Fetching data from API');
        fetch('http://localhost:8000/api/bloglist')
        .then(response => response.json())
        .then(data => {
            console.log('Got data back', data);
            this.setState({
            gripePile: data,
            });
        });
    }

    resetState = () => {
        this.getBlogs();
    };

    render () {
        console.log(this.blogs)
        return (
            <BlogList
                blogs={this.state.blogs}
                resetState={this.resetState}
                />
        );
    }
}

export default Home;
